//jQuery has an interface called jQuery that defines all its prototype functions. easyPieChart
interface JQuery {
    pulsate(...args: any[]): any;
    draggable(...args: any[]): any;
    tooltip(...args: any[]): any;
    summernote(...args: any[]): any;
    modal(...args: any[]): any;
    modalmanager(...args: any[]): any;
    datepicker(...args: any[]): any;
    slimScroll(...args: any[]): any;
    animateCss(...args: any[]): any;
    counterUp(...args: any[]): any;
    select2(...args: any[]): any;
    easyPieChart(...args: any[]): any;
}